
public class test {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x = 5;
		int y = 30;
		int count = 0;
		
		while (x < y)
		{
			x = x + 2;
			count = count + 1;
		}
		
		System.out.println(x + " " + y);
		
		System.out.println(count);
	}

}
